mapper.select:
select t1.*, t2.name dept_name  from emp t1 left join dept t2 on t1.dept_id = t2.id
 
     
         t1.name like concat('%',#{name},'%')
     
     
         and gender = #{gender}
     
     
         and entrydate between #{begin} and #{end}
     
 
 order by t1.update_time desc
